import React from 'react';
import './AvailableHomes.css'; // Assume we have some CSS for the grid

function AvailableHomes({ homes }) {
  return (
    <div className="card-grid">
      {homes.map((home, index) => {
        // Convert the address into a URL-friendly slug (e.g., "123 Main St" => "123-main-st")
        const addressSlug = home.address.toLowerCase().replace(/\s+/g, '-');
        return (
          <div className="card" key={index} id={addressSlug}>
            <h2 className="card-title">
              {/* Use a link if you have a dedicated page for the home */}
              <a href={`/homes/${addressSlug}`}>
                {home.address}
              </a>
            </h2>
            <div className="card-content">
              <p>{home.info}</p>
              {/* You could include an image, price, etc. */}
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default AvailableHomes;
