import React, { useState } from 'react';
import Lightbox from 'yet-another-react-lightbox';
import 'yet-another-react-lightbox/styles.css';

function ImageCarousel({ pics }) {
  // State for controlling Lightbox open status and the current image index.
  const [open, setOpen] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Handler for when an image is clicked.
  const handleImageClick = (index) => {
    setCurrentIndex(index);
    setOpen(true);
  };

  return (
    <div>
      {/* Display thumbnails dynamically */}
      <div className="carousel">
        {pics.map((pic, index) => (
          <img
            key={index}
            src={pic.thumbnail}
            alt={`Thumbnail ${index}`}
            onClick={() => handleImageClick(index)}
            style={{ cursor: 'pointer', margin: '0 5px', width: '150px', height: 'auto' }}
          />
        ))}
      </div>
      {/* Lightbox opens when an image is clicked */}
      {open && (
        <Lightbox
          slides={pics.map(pic => ({ src: pic.original }))}
          open={open}
          index={currentIndex}
          close={() => setOpen(false)}
        />
      )}
    </div>
  );
}

export default ImageCarousel;
