import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "yet-another-react-lightbox/styles.css";

import React, { useState } from "react";
import Slider from "react-slick";
import Lightbox from "yet-another-react-lightbox";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "yet-another-react-lightbox/styles.css";

function ImageCarousel({ pics }) {
  // References to each slider
  const [slider1, setSlider1] = useState(null);
  const [slider2, setSlider2] = useState(null);

  // Lightbox state
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Main slider settings
  const mainSettings = {
    asNavFor: slider2,
    ref: (slider) => setSlider1(slider),
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true,
    fade: true,
    infinite: true,
    dots: false,
    beforeChange: (oldIndex, newIndex) => {
      setCurrentIndex(newIndex);
    },
    // Clicking the main image triggers the Lightbox
    onClick: () => setLightboxOpen(true),
  };

  // Thumbnail slider settings
  const thumbSettings = {
    asNavFor: slider1,
    ref: (slider) => setSlider2(slider),
    slidesToShow: 6,     // How many thumbnails to show at once
    swipeToSlide: true,
    focusOnSelect: true,
    infinite: true,
    arrows: true,
    dots: false,
  };

  return (
    <div style={{ maxWidth: "800px", margin: "0 auto" }}>
      {/* Main (large) slider */}
      <Slider {...mainSettings}>
        {pics.map((pic, idx) => (
          <div key={idx}>
            <img
              src={pic.original}
              alt={`Slide ${idx}`}
              style={{ width: "100%", height: "auto" }}
            />
          </div>
        ))}
      </Slider>

      {/* Thumbnail slider */}
      <Slider {...thumbSettings} style={{ marginTop: "1rem" }}>
        {pics.map((pic, idx) => (
          <div key={idx}>
            <img
              src={pic.thumbnail}
              alt={`Thumbnail ${idx}`}
              style={{ width: "100%", height: "auto" }}
            />
          </div>
        ))}
      </Slider>

      {/* Lightbox for fullscreen */}
      <Lightbox
        slides={pics.map((pic) => ({ src: pic.original }))}
        open={lightboxOpen}
        index={currentIndex}
        close={() => setLightboxOpen(false)}
      />
    </div>
  );
}

export default ImageCarousel;
