// src/components/Contact.jsx
import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as yup from 'yup';
import emailjs from '@emailjs/browser';
import '../css/Contact.css';
import jimPhoto from '../assets/mainPics/jim.jpg';
import mbLogo from '../assets/mainPics/MB-logo.png';
import facebookLogo from '../assets/mainPics/Facebook_Logo_Primary.png';

export default function Contact() {
  const [loading, setLoading] = useState(false);
  const [notif, setNotif]   = useState({ message: '', type: '' });

  const validationSchema = yup.object({
    firstName: yup.string()
      .max(25,   'Max 25 characters')
      .required('Required'),
    lastName:  yup.string()
      .max(30,   'Max 30 characters')
      .required('Required'),
    email:     yup.string()
      .email(   'Invalid email')
      .required('Required'),
    phone:     yup.string()
      .matches(/^\+?[0-9 ]+$/, 'Invalid phone number')
      .nullable(),
    message:   yup.string()
      .max(300,  'Max 300 characters')
      .required('Required'),
  });

  const formik = useFormik({
    initialValues: {
      firstName: '',
      lastName:  '',
      email:     '',
      phone:     '',
      message:   '',
    },
    validationSchema,
    onSubmit: async (_values, { resetForm }) => {
      setLoading(true);
      try {
        await emailjs.sendForm(
          'service_pda4dyn',
          'template_4npi60d',
          '#contactForm',
          'JiyFMUBN4XcgzPCpE'
        );
        setNotif({ message: 'Message sent successfully!', type: 'success' });
        resetForm();
      } catch (err) {
        setNotif({ message: 'Oops—something went wrong.', type: 'error' });
      } finally {
        setLoading(false);
        setTimeout(() => setNotif({ message: '', type: '' }), 5000);
      }
    }
  });

  // Focus first invalid field on button click
  const focusFirstError = () => {
    formik.setTouched({
      firstName: true,
      lastName:  true,
      email:     true,
      phone:     true,
      message:   true
    });
    formik.validateForm().then(errs => {
      const first = Object.keys(errs)[0];
      if (first) document.getElementById(first)?.focus();
    });
  };

  return (
    <div className="contact-page">
      {notif.message && (
        <div className={`notification ${notif.type}`}>
          {notif.message}
        </div>
      )}

      <img src={mbLogo} alt="Masterbilt Logo" className="logo" />

      <aside className="contact-card">
        <img src={jimPhoto} alt="James A. Stiltner" />
        <h2>James A. Stiltner</h2>
        <a href="tel:7199643526">719‑964‑3526</a>
        <a href="mailto:jim@masterbilt.com">jim@masterbilt.com</a>
      </aside>

      <form
        id="contactForm"
        className="contact-form"
        onSubmit={formik.handleSubmit}
        noValidate
      >
        {[
          { id: 'firstName', label: 'First Name' },
          { id: 'lastName',  label: 'Last Name'  },
          { id: 'email',     label: 'Email', type: 'email' },
          { id: 'phone',     label: 'Phone (optional)' }
        ].map(({ id, label, type = 'text' }) => (
          <div key={id} className="input-group">
            <label htmlFor={id}>{label}</label>
            <input
              id={id}
              name={id}
              type={type}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              value={formik.values[id]}
              className={
                formik.touched[id] && formik.errors[id]
                  ? 'error'
                  : ''
              }
              aria-invalid={
                !!(formik.touched[id] && formik.errors[id])
              }
              aria-describedby={
                formik.errors[id] ? `${id}-error` : undefined
              }
            />
            {formik.touched[id] && formik.errors[id] && (
              <div id={`${id}-error`} className="error-text">
                {formik.errors[id]}
              </div>
            )}
          </div>
        ))}

        <div className="input-group">
          <label htmlFor="message">Message</label>
          <textarea
            id="message"
            name="message"
            rows="6"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.message}
            className={
              formik.touched.message && formik.errors.message
                ? 'error'
                : ''
            }
            aria-invalid={
              !!(formik.touched.message && formik.errors.message)
            }
            aria-describedby={
              formik.errors.message ? 'message-error' : undefined
            }
          />
          {formik.touched.message && formik.errors.message && (
            <div id="message-error" className="error-text">
              {formik.errors.message}
            </div>
          )}
        </div>

        <button
          type="submit"
          className="btn"
          disabled={loading}
          onClick={focusFirstError}
        >
          {loading ? 'Sending…' : 'Send Message'}
        </button>
      </form>

      <div className="map-container">
        <iframe
          title="Office Location"
          src="https://www.google.com/maps/embed?pb=!1m18!…"
          width="100%"
          height="250"
          style={{ border: 0 }}
          allowFullScreen=""
          loading="lazy"
        />
      </div>

      <div className="social-links">
        <a
          href="https://www.facebook.com/MasterbiltHomes"
          target="_blank"
          rel="noopener noreferrer"
          className="facebook"
        >
          <img src={facebookLogo} alt="Facebook" />
          Visit Our Facebook
        </a>
        <a
          href="https://www.bbb.org/us/co/palmer-lake/profile/building-contractors/masterbilt-homes-0785-5281152"
          target="_blank"
          rel="noopener noreferrer"
          className="bbb"
        >
          <img
            src="https://seal-southerncolorado.bbb.org/seals/darkgray-seal-200-65-bbb-5281152.png"
            alt="BBB Rating"
          />
        </a>
      </div>
    </div>
  );
}
