import React, { useState } from "react";
import Slider from "react-slick";
import Lightbox from "yet-another-react-lightbox";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "yet-another-react-lightbox/styles.css";
import fullscreenImg from "./image.png";

// Custom arrow components with visible, larger icons
function NextArrow({ onClick }) {
  return (
    <div
      onClick={onClick}
      style={{
        position: "absolute",
        right: "10px",
        top: "45%",
        transform: "translateY(-50%)",
        zIndex: 2,
        cursor: "pointer",
        color: "#000",
        fontSize: "100px",
        userSelect: "none",
      }}
    >
      ›
    </div>
  );
}

function PrevArrow({ onClick }) {
  return (
    <div
      onClick={onClick}
      style={{
        position: "absolute",
        left: "10px",
        top: "45%",
        transform: "translateY(-50%)",
        zIndex: 2,
        cursor: "pointer",
        color: "#000",
        fontSize: "100px",
        userSelect: "none",
      }}
    >
      ‹
    </div>
  );
}

function SynchronizedCarousel({ pics }) {
  const [slider1, setSlider1] = useState(null);
  const [slider2, setSlider2] = useState(null);
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Main slider settings with custom arrows and fade effect
  const mainSettings = {
    asNavFor: slider2,
    ref: (slider) => setSlider1(slider),
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true,
    fade: true,
    infinite: true,
    dots: false,
    beforeChange: (oldIndex, newIndex) => setCurrentIndex(newIndex),
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
  };

  // Thumbnail slider settings with custom arrows
  const thumbSettings = {
    asNavFor: slider1,
    ref: (slider) => setSlider2(slider),
    slidesToShow: 6, // default for desktop
    swipeToSlide: true,
    focusOnSelect: true,
    infinite: true,
    arrows: true,
    dots: false,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    responsive: [
      {
        breakpoint: 768, // at or below 768px width
        settings: {
          slidesToShow: 3, // show fewer thumbnails
        },
      },
      {
        breakpoint: 480, // at or below 480px width
        settings: {
          slidesToShow: 2, // even fewer thumbnails for small screens
        },
      },
    ],
  };


  return (
    <div
      className="synchronized-carousel"
      style={{ maxWidth: "800px", margin: "0 auto" }}
    >
      {/* Main slider */}
      <div className="main-slider-wrapper" style={{ position: "relative"}}>
        <Slider {...mainSettings}>
          {pics.map((pic, idx) => (
            <div key={idx} className="main-slide">
              <img
                src={pic.original}
                alt={`Slide ${idx}`}
                style={{ width: "100%", height: "auto", cursor: "pointer", padding: "0px"}}
                onClick={() => setLightboxOpen(true)}
              />
            </div>
          ))}
        </Slider>
        {/* Optional overlay fullscreen button */}
        <button
          className="fullscreen-button"
          onClick={() => setLightboxOpen(true)}
          style={{
            position: "absolute",
            bottom: "12%",
            right: "0%",
            padding: "8px 12px",
            backgroundColor: "transparent",
            border: "none",
            cursor: "pointer",
          }}
        >
          <img src={fullscreenImg} alt="Fullscreen" style={{ width: "40px", height: "40px" }} />
        </button>
      </div>

      {/* Thumbnail slider */}
        <div
        className="thumb-slider-wrapper"
        style={{
            marginTop: "-8%", // adjust this value as needed
            marginBottom: 0,
            padding: 0,
        }}
        >
        <Slider {...thumbSettings}>
            {pics.map((pic, idx) => (
            <div key={idx} className="thumb-slide" style={{ margin: 0, padding: 0 }}>
                <img
                src={pic.thumbnail}
                alt={`Thumbnail ${idx}`}
                style={{
                    width: "100%",
                    height: "80px",
                    objectFit: "cover",
                    cursor: "pointer",
                    display: "block",
                }}
                />
            </div>
            ))}
        </Slider>
        </div>


      {/* Lightbox for fullscreen viewing */}
      <Lightbox
        slides={pics.map((pic) => ({ src: pic.original }))}
        open={lightboxOpen}
        index={currentIndex}
        close={() => setLightboxOpen(false)}
      />
    </div>
  );
}

export default SynchronizedCarousel;
