import React, { useState } from "react";
import Lightbox from "yet-another-react-lightbox";
import "yet-another-react-lightbox/styles.css";

export default function Gallery() {
  // Slides can be objects with { src, title, description, etc. }
  const slides = [
    { src: "https://via.placeholder.com/800x600/0000FF/808080" },
    { src: "https://via.placeholder.com/800x600/FF0000/FFFFFF" },
    { src: "https://via.placeholder.com/800x600/00FF00/000000" },
    // ... add as many images as you like
  ];

  const [open, setOpen] = useState(false);
  const [index, setIndex] = useState(0);

  return (
    <div>
      {/* Thumbnails or a grid of images */}
      <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
        {slides.map((slide, i) => (
          <img
            key={i}
            src={slide.src}
            alt={`thumbnail-${i}`}
            style={{ width: "150px", cursor: "pointer" }}
            onClick={() => {
              setIndex(i);
              setOpen(true);
            }}
          />
        ))}
      </div>

      {/* Lightbox with carousel-like navigation once open */}
      <Lightbox
        slides={slides}
        open={open}
        index={index}
        close={() => setOpen(false)}
        // You can add plugins or custom props here:
        // plugins={[Thumbnails, Zoom, Fullscreen, etc.]}
      />
    </div>
  );
}
